function findHypotenuse(a, b){
   if (isNaN(a) || isNaN(b)) {
     console.log("Error, make sure a and b are numbers.");
   }
   else {
    return Math.sqrt(a * a + b * b);
   }
};

function findSide(hypotenuse, x){
   if (isNaN(hypotenuse) || isNaN(x)) {
     console.log("Error, make sure the hypotenuse and x are numbers.");
   }
   else {
    return Math.sqrt(hypotenuse * hypotenuse - x * x);
   }
};


module.exports.findHypotenuse = findHypotenuse;
module.exports.findSide = findSide;